DROP TABLE IF EXISTS `crm_customer`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `crm_customer` (
  `user_id` bigint(20) NOT NULL,
  `course_set` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `create_time` date DEFAULT NULL,
  `employee_num` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `full_payment` tinyint(4) NOT NULL DEFAULT '1' COMMENT ' ',
  `member_begin_time` datetime DEFAULT NULL,
