  `problems` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `school_area` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `school_category` varchar(50) COLLATE utf8_unicode_ci DEFAULT NULL,
  `service_require` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `setup_time` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `strength` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `stu_num` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `sub_school_num` int(11) NOT NULL DEFAULT '1',
  `update_time` datetime DEFAULT NULL,
  `weakness` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `constructor_id` bigint(20) DEFAULT NULL,
  PRIMARY KEY (`user_id`),
  KEY `FKs46mcbo1agv7m9y7h2ikgbefm` (`constructor_id`),
  CONSTRAINT `FKao90luxvom7gnr630cs9oj8pg` FOREIGN KEY (`constructor_id`) REFERENCES `employee` (`user_id`),
  CONSTRAINT `FKs46mcbo1agv7m9y7h2ikgbefm` FOREIGN KEY (`constructor_id`) REFERENCES `employee` (`user_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
